css:
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 0;
    color: #333;
}

.header {
    display: flex;
    align-items: center;
    margin-bottom: 20px;
    width: 100%;
}
.header .image, .image {
    width: 200px;
    height: 80px;
    flex-shrink: 0;
}
.header .image {
    height: 130px;
}
.header .right {
    flex-grow: 1;
    text-align: right;
    padding-left: 20px;
}

.arabic-title {
    color: #32368d;
    font-size: 26px;
    margin-bottom: 2px;
    border-bottom: 1px solid red;
    display: inline-block;
}
.english-title {
    color: #32368d;
    margin-top: 0;
    font-weight: 600;
    font-size: 18px;
}
.company-info {
    color: #32368d;
    font-size: 14px;
    margin: 0;
}

.main-content {
    padding: 0 10px;
}
.title {
    text-align: center;
    font-size: 18px;
    color: #1e1565;
    font-weight: bold;
    margin-bottom: 15px;
}

table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 10px;
}
th, td {
    border: 1px solid #ddd;
    padding: 1px;
    text-align: left;
    font-size: 12px;
    line-height: 1em;
    vertical-align: middle;
    height: 100%;
}
th {
    background-color: #f2f2f2;
    color: #1e1565;
    font-weight: bold;
}
.pink {
    background-color: #f9f2f5;
}

.detail {
    margin-top: 10px;
}

.footer {
    margin-top: 20px;
    text-align: center;
    font-size: 11px;
    color: #1e1565;
    border-top: 1px solid #ddd;
    padding-top: 10px;
}
.footer-bottom {
    margin-top: 5px;
}

.text-right { text-align: right; }
.text-center { text-align: center; }
.textStart { text-align: start; }
.textEnd { text-align: end; }
.text-sm { font-size: 12px; }
.small-text { font-size: 10px; }

.signature-block {
    margin-top: 30px;
}

.terms-conditions {
    margin-top: 15px;
    font-size: 11px;
}
.terms-conditions ol {
    padding-left: 20px;
}
.terms-conditions li {
    margin-bottom: 5px;
}

.flex, .third {
    display: flex;
    flex-wrap: wrap;
    width: 100%;
}
.flexbetween, .justifySpaceBetween {
    display: flex;
    justify-content: space-between;
}

.flexdown {
    width: 50%;
    display: flex;
    flex-direction: column;
    gap: 5px;
}

.page-header, .page-footer {
    position: fixed;
    width: 100%;
}
.page-header {
    top: 0mm;
}
.page-footer {
    bottom: 0mm;
    width: 80%;
}

.page {
    page-break-after: always;
}

@media print {
    thead { display: table-header-group; }
    tfoot { display: table-footer-group; }
    button { display: none; }
    body { margin: 0; }
}

.name {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 10px;
    line-height: 1.5em;
    width: 100%;
}
.name h5 {
    margin: 0;
    line-height: 1.2em;
    white-space: nowrap;
    margin-bottom: 3px;
}
.name h6 {
    margin: 2px 0;
    font-size: 10px;
    padding: 0;
    line-height: 1.1;
}

.colon {
    min-width: 10px;
    text-align: right;
}
.width100 {
    width: 99.9%;
}
.borderT {
    border-top: 1px solid black;
}
.borderB {
    border-bottom: 1px solid black;
}
.padding5 {
    padding: 5px;
}

.bank-table {
    width: 100%;
    border-collapse: collapse;
    font-size: 14px;
    text-align: left;
}

.head {
    text-align: left;
    font-weight: bold;
    white-space: nowrap;
    width: 25px;
}
.value {
    text-align: left;
}
.three > div:nth-child(2) {
    margin-left: -35px;
}
