css:
body {
    font-family: Arial, sans-serif;
    font-size: 10pt;
    margin: 0;
}

.survey-report {
    width: 210mm;
    padding: 2mm;
    box-sizing: border-box;
    margin-top: 10px;
}

.title {
    text-align: center;
    font-size: 14pt;
    font-weight: bold;
    margin-bottom: 8px;
}

.subtitle {
    text-align: center;
    font-size: 11pt;
    font-weight: bold;
    margin-bottom: 8px;
}

.section-header {
    background: #f0f0f0;
    font-weight: bold;
    border: 1px solid #000;
    font-size: 10pt;
    text-align: center;
    border-collapse: collapse;
    border-top: none;
}

.table {
    width: 100%;
    border-collapse: collapse;
    margin: 0px !important;
    border-top: none;
}

.table td {
    border: 1px solid black;
    padding: 7px !important; /* reduced padding */
    width: 25%;
    line-height: 1.2!important;
    vertical-align: middle !important;/* tighter line spacing */
    
}

.footer-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 0px;
    border: 1px solid black; 
    border-top: none;
    margin-bottom: 20px;
}

.footer-table th, .footer-table td {
    padding: 3px 4px;
    width: 33.33%;
    line-height: 1.2;
    border-left: 1px solid black;
}

