.commodities-box {
    width: 100%;
    border-collapse: collapse;
    border: 1px solid black;
}

.commodities-box th,
.commodities-box td {
    border: 1px solid black;
    text-align: center;
    font-size: 10pt;
    padding: 4px;
}

.commodities-box .header-row {
    background: #f0f0f0;
    font-weight: bold;
}

